mapper.select:
select * from ( select rownum as rn, b.* from ( select * from library
where upper(title) like '%' || upper(#{sval}) || '%' where upper(writer) like '%' || upper(#{sval}) || '%' where upper(publisher) like '%' || upper(#{sval}) || '%' where upper(title) || upper(writer) || upper(publisher) like '%' || upper(#{sval}) || '%'
order by book_no desc ) b ) where rn between #{start} and #{end}
